(function(){tinymce.PluginManager.requireLangPack('showhide');tinymce.create('tinymce.plugins.ShowHide',{init:function(ed,url){var t=this,tbId=ed.getParam('dashboard_adv_toolbar','toolbar2'),last=0,moreHTML,nextpageHTML;ed.settings.dashboard_adv_hidden=1;ed.onPostRender.add(function(){if(ed.getParam('dashboard_adv_hidden',1)){tinymce.DOM.hide(ed.controlManager.get(tbId).id)}});ed.addCommand('mceShowHide',function(){var id=ed.controlManager.get(tbId).id,cm=ed.controlManager;if(tinymce.DOM.isHidden(id)){cm.setActive('wp_adv',1);tinymce.DOM.show(id);ed.settings.dashboard_adv_hidden=0}else{cm.setActive('wp_adv',0);tinymce.DOM.hide(id);ed.settings.dashboard_adv_hidden=1}});ed.addButton('showhide',{title:'showhide.desc',cmd:'mceShowHide',image:url+'/img/toolbars.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('showhide',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'ShowHide plugin',author:'Chris Troup',authorurl:'http://www.dashboardwebapp.com',infourl:'http://www.dashboardwebapp.com',version:"1.0"}}});tinymce.PluginManager.add('showhide',tinymce.plugins.ShowHide)})();